import {Component, OnInit, ViewChild} from '@angular/core';
import { MatTable, MatTableDataSource} from '@angular/material/table';
import axios from 'axios';

export interface PeriodicElement {
  userId: number;
  id: number;
  title: string
  completed: boolean;
}



const ELEMENT_DATA: PeriodicElement[] = [
  {
    "userId": 1,
    "id": 1,
    "title": "delectus aut autem",
    "completed": false
  }
];

/**
 * @title Adding and removing data when using an array-based datasource.
 */
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  displayedColumns: string[] = ['userId', 'id', 'title', 'completed'];
  dataSource = new MatTableDataSource(ELEMENT_DATA);
  @ViewChild(MatTable) table: MatTable<PeriodicElement>;
  ngOnInit() {
    this.elementData();
  }
  async elementData() {
    const { data } = await axios.get('https://jsonplaceholder.typicode.com/todos');
    data.forEach((e: PeriodicElement) => {
      this.dataSource.data.push(e);
    });
    this.dataSource._renderChangesSubscription;
  }

  applyFilter(event: Event) {
     const filterValue = (event.target as HTMLInputElement).value;
     this.dataSource.filter = filterValue.trim().toLowerCase();
  }
}
<mat-form-field appearance="standard">
  <mat-label>Filter</mat-label>
  <input matInput placeholder="Ingrese filtro" #input>
</mat-form-field>
<button mat-raised-button (click)="elementData()"> Refresh </button>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8 demo-table">

  <!-- User Name Column -->
  <ng-container matColumnDef="userId">
    <th mat-header-cell *matHeaderCellDef> User </th>
    <td mat-cell *matCellDef="let element"> {{element.userId}} </td>
  </ng-container>

  <!-- Subject Column -->
  <ng-container matColumnDef="id">
    <th mat-header-cell *matHeaderCellDef> Subject </th>
    <td mat-cell *matCellDef="let element"> {{element.id}} </td>
  </ng-container>

  <!-- Customers Column -->
  <ng-container matColumnDef="title">
    <th mat-header-cell *matHeaderCellDef> Customer </th>
    <td mat-cell *matCellDef="let element"> {{element.title}} </td>
  </ng-container>

  <!-- Priority Column -->
  <ng-container matColumnDef="completed">
    <th mat-header-cell *matHeaderCellDef> Priority </th>
    <td mat-cell *matCellDef="let element"> {{element.completed}} </td>
  </ng-container>


  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

  <!-- Row shown when there is no matching data. -->
  <tr class="mat-row" *matNoDataRow>
    <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
  </tr>
</table>
