import {Component, ViewChild} from '@angular/core';
import { MatTable, MatTableDataSource} from '@angular/material/table';
import axios from 'axios';

export interface PeriodicElement {
  UserName: string;
  Subject: string;
  Customer: string
  Priotiry: string;
  Status: string;
}



const ELEMENT_DATA: PeriodicElement[] = [
  {
    "UserName" : "Andres Gómez",
    "Subject" : "I cant login to my account",
    "Customer" : "100%",
    "Priotiry" : "High",
    "Status" : "Solved"
  },
  {
    "UserName" : "Ezequiel Salatino",
    "Subject" : "I need help",
    "Customer" : "80%",
    "Priotiry" : "Medium",
    "Status" : "Open"
  },
  {
    "UserName" : "AAAA",
    "Subject" : "I cant login to my account",
    "Customer" : "100%",
    "Priotiry" : "Low",
    "Status" : "Pending"
  },
];

/**
 * @title Adding and removing data when using an array-based datasource.
 */
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  displayedColumns: string[] = ['User Name', 'Subject', 'Customer', 'Priority', 'Status'];
  dataSource = [...ELEMENT_DATA];

  @ViewChild(MatTable) table!: MatTable<PeriodicElement>;

  async elementData() {
    const { data } = await axios.get('https://information.free.beeceptor.com/');
    console.log(data)
    this.dataSource = data;
    this.table.renderRows();
  }

  // applyFilter(event: Event) {
  //   const filterValue = (event.target as HTMLInputElement).value;
  //   this.table.filter = filterValue.trim().toLowerCase();
  // }
}
<mat-form-field appearance="standard">
  <mat-label>Filter</mat-label>
  <input matInput (keyup)="applyFilter($event)" placeholder="Ingrese filtro" #input>
</mat-form-field>
<button mat-raised-button (click)="elementData()"> Refresh </button>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8 demo-table">

  <!-- User Name Column -->
  <ng-container matColumnDef="User Name">
    <th mat-header-cell *matHeaderCellDef> User </th>
    <td mat-cell *matCellDef="let element"> {{element.UserName}} </td>
  </ng-container>

  <!-- Subject Column -->
  <ng-container matColumnDef="Subject">
    <th mat-header-cell *matHeaderCellDef> Subject </th>
    <td mat-cell *matCellDef="let element"> {{element.Subject}} </td>
  </ng-container>

  <!-- Customers Column -->
  <ng-container matColumnDef="Customer">
    <th mat-header-cell *matHeaderCellDef> Customer </th>
    <td mat-cell *matCellDef="let element"> {{element.Customers}} </td>
  </ng-container>

  <!-- Priority Column -->
  <ng-container matColumnDef="Priority">
    <th mat-header-cell *matHeaderCellDef> Priority </th>
    <td mat-cell *matCellDef="let element"> {{element.Priotiry}} </td>
  </ng-container>

   <!-- Status Column -->
   <ng-container matColumnDef="Status">
    <th mat-header-cell *matHeaderCellDef> Status </th>
    <td mat-cell *matCellDef="let element"> {{element.Status}} </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

  <!-- Row shown when there is no matching data. -->
  <tr class="mat-row" *matNoDataRow>
    <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
  </tr>
</table>
