import {Component} from '@angular/core';
import { MatTableDataSource} from '@angular/material/table';
import axios from 'axios';
export interface PeriodicElement {
  UserName: string;
  Subject: string;
  Customer: string;
  Priotiry: string;
  Status: string
}

const ELEMENT_DATA: PeriodicElement = async () => {
  const info = await axios.get('https://information.free.beeceptor.com/');
  return info.data
}

/**
 * @title Adding and removing data when using an array-based datasource.
 */
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  displayedColumns: string[] = ['UserName', 'Subject', 'Customer', 'symbol'];
  dataSource = new MatTableDataSource(ELEMENT_DATA());


  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }
}
<mat-form-field appearance="standard">
  <mat-label>Filter</mat-label>
  <input matInput (keyup)="applyFilter($event)" placeholder="Ex. ium" #input>
</mat-form-field>

<table mat-table [dataSource]="dataSource" class="mat-elevation-z8 demo-table">

  <!-- User Name Column -->
  <ng-container matColumnDef="User Name">
    <th mat-header-cell *matHeaderCellDef> No. </th>
    <td mat-cell *matCellDef="let element"> {{element.UserName}} </td>
  </ng-container>

  <!-- Subject Column -->
  <ng-container matColumnDef="Subject">
    <th mat-header-cell *matHeaderCellDef> Name </th>
    <td mat-cell *matCellDef="let element"> {{element.Subject}} </td>
  </ng-container>

  <!-- Customers Column -->
  <ng-container matColumnDef="Customers">
    <th mat-header-cell *matHeaderCellDef> Weight </th>
    <td mat-cell *matCellDef="let element"> {{element.Customers}} </td>
  </ng-container>

  <!-- Priority Column -->
  <ng-container matColumnDef="Priority">
    <th mat-header-cell *matHeaderCellDef> Symbol </th>
    <td mat-cell *matCellDef="let element"> {{element.Priotiry}} </td>
  </ng-container>

   <!-- Status Column -->
   <ng-container matColumnDef="Status">
    <th mat-header-cell *matHeaderCellDef> Symbol </th>
    <td mat-cell *matCellDef="let element"> {{element.Status}} </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

  <!-- Row shown when there is no matching data. -->
  <tr class="mat-row" *matNoDataRow>
    <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
  </tr>
</table>
